import { useContext} from 'react';
import { Link, useHistory } from 'react-router-dom'
import { ModalContext, ShopContext } from '../../contexts/ShoppingContext';


function Navbar() {
    const {cartProducts} = useContext(ShopContext);
    const { setCartVisible } = useContext(ModalContext);
    const history = useHistory();

    const siteLogo = "https://i.ibb.co/YfPBXvN/mockup-with-gold-logo-136295-386.jpg";
    return (
        <div className={`px-6 sm:px-12 lg:px-14 xl:px-20 py-5 overflow-y-hidden flex items-start md:items-center bg-gradient-to-r from-yellow-400 to-yello-700 fixed top-0 w-full z-10 md:h-24 transform ease-linear duration-300 transition-all max-h-20`}>
            <div className="flex flex-wrap items-center flex-grow">
                <img onClick={() => history.push('/')} className="rounded-full cursor-pointer transition transform hover:scale-105 hover:bg-opacity-80 border-2 border-night-light-400 mr-2 hidden sm:inline-block  md:w-12 md:h-12 rounded " src={siteLogo} alt="Site Logo" />
                <div className="md:space-x-5 mt-4  flex flex-col md:flex-row w-full md:w-auto md:inline-block">
                    <div className="text-lg inline-block font-semibold my-1 text-night-light-200  px-2 rounded transition transform hover:scale-110 hover:bg-opacity-80"><Link to="/">Home</Link></div>
                    <div className="text-lg inline-block font-semibold my-1 text-night-light-200 px-2 rounded transition transform hover:scale-110 hover:bg-opacity-80"><Link to="/">Products</Link></div>
                    <div className="text-lg inline-block font-semibold my-1 text-night-light-200 px-2 rounded transition transform hover:scale-110 hover:bg-opacity-80"><Link to="/about">About</Link></div>
                </div>
            </div>
            <button onClick={() => setCartVisible(true)} className="transition transform hover:scale-110 hover:bg-opacity-80 text-3xl mx-2 my-1  text-night-light-100 px-2 relative rounded-lg justify-self-end" >
                <i className="fa fa-shopping-cart"></i>
                {
                    Object.keys(cartProducts).length ? (
                        <p className="absolute -right-3 -top-3 text-base rounded-full px-2 bg-indigo-500 text-neon1-light-300 font-semibold">{Object.values(cartProducts).reduce((totalSelected, product) => totalSelected + product.count, 0)}</p>
                    ) : ""
                }
            </button>
        </div>
    )
}

export default Navbar;
